import { useState } from "react";
import Card from "../card";
import "./style.css";

const CardsList = ({ cardsList }) => {
  const [suitFilter, setSuitFilter] = useState(false);
  const [currentSuit, setCurrentSuit] = useState("");

  const handleSuitFilter = (e) => {
    if (e.target.id === currentSuit) {
      setCurrentSuit("");
      setSuitFilter(false);
      e.target.checked = false;
    } else {
      setCurrentSuit(e.target.id);
      setSuitFilter(true);
    }
  };

  return (
    <div>
      <div className="filter-container">
        <p>Filtrar por naipe</p>
        <div>
          <input
            onClick={handleSuitFilter}
            type="radio"
            id="SPADES"
            name="suit"
          />
          <label htmlFor="SPADES">Espadas</label>

          <input
            onClick={handleSuitFilter}
            type="radio"
            id="HEARTS"
            name="suit"
          />
          <label htmlFor="HEARTS">Copas</label>

          <input
            onClick={handleSuitFilter}
            type="radio"
            id="CLUBS"
            name="suit"
          />
          <label htmlFor="CLUBS">Paus</label>

          <input
            onClick={handleSuitFilter}
            type="radio"
            id="DIAMONDS"
            name="suit"
          />
          <label htmlFor="DIAMONDS">Ouros</label>
        </div>
      </div>
      <div>
        {!suitFilter &&
          cardsList.map((actual, index) => {
            return <Card card={actual} key={index} />;
          })}

        {suitFilter &&
          cardsList
            .filter((actual) => actual.suit === currentSuit)
            .map((actual, index) => <Card card={actual} key={index} />)}
      </div>
    </div>
  );
};

export default CardsList;
